import "bootstrap/dist/css/bootstrap.min.css";
import { Container, Table, Button } from 'react-bootstrap';
import Example from "../Modal/Modal";
import { useState } from "react";

const Tabel = () => {

    // const data = props.dataDatas;

    const [getDatas, setDatas] = useState([
        {id:1, Nama:'Haekal', Email:'dummy@gmail.com'}
    ])

    const evenCreateForm = (form) => {
        setDatas(getDatas.concat(form))
        console.log(getDatas)
    }

    const handleDeleteClick = (datasId) => {
        const newDatas = [...getDatas];

        const index = getDatas.findIndex((datass) => datass.id === datasId);

        newDatas.splice(index, 1);
        setDatas(newDatas);

    }

    return(

        <Container> 
        <Example onCreateForm={evenCreateForm}/>
        <Table striped bordered hover variant="dark">
            <thead>
                <tr>
                <th>Nama</th>
                <th>Email</th>
                <th>Action</th>
                </tr>
            </thead>
            <tbody>
            {getDatas.map(datas => {
                                return(
                                    <tr>
                                        <td>{datas.Nama}</td>
                                        <td>{datas.Email}</td>
                                        <td><Button as="input" type="reset" value="reset"/><h3>&ensp;</h3><Button variant="dark" onClick={() => handleDeleteClick(datas.id)} >Delete</Button></td>
                                    </tr>
                                );
                            })
                            }
            </tbody>
        </Table>
    </Container>
    );
}
export default Tabel;
