import "bootstrap/dist/css/bootstrap.min.css";
import { Container, Table, Button } from 'react-bootstrap';
import Example from "../Modal/Modal";

const Tabel = () => {

    // const data = props.dataDatas;

    const [getDatas, setDatas] = useState([
        {id:1, Name:'Haekal', Email:'dummy@gmail.com'},
        {id:2, Name:'dummy3', Email:'dumm@gmail.com'},
        {id:3, Name:'Test', Email:'dummy2@gmail.com'}
    ])

    const evenCreateForm = (form) => {
        setDatas(getDatas.concat(form))
        console.log(getDatas)
    }

    return(

        <Container> 
        <Example onCreateForm={evenCreateForm}/>
        <Table striped bordered hover variant="dark">
            <thead>
                <tr>
                <th>Nama</th>
                <th>Email</th>
                <th>Action</th>
                </tr>
            </thead>
            <tbody>
            {getDatas.map(datas => {
                                return(
                                    <tr>
                                        <td>{datas.Nama}</td>
                                        <td>{datas.Email}</td>
                                        <td><Button as="input" type="reset" value="reset"/><h3>&ensp;</h3><Button variant="dark">Delete</Button></td>
                                    </tr>
                                );
                            })
                            }
            </tbody>
        </Table>
    </Container>
    );
}
export default Tabel;
